class Solution:
    def __init__(self):
        self.sec_code = input("Security Code: ")
        self.keypad = input("Keypad: ")
        self.distances = [
            [0, 1, 2, 1, 1, 2, 2, 2, 2],
            [1, 0, 1, 1, 1, 1, 2, 2, 2],
            [2, 1, 0, 2, 1, 1, 2, 2, 2],
            [1, 1, 2, 0, 1, 2, 1, 1, 2],
            [1, 1, 1, 1, 0, 1, 1, 1, 1],
            [2, 1, 1, 2, 1, 0, 2, 1, 1],
            [2, 2, 2, 1, 1, 2, 0, 1, 2],
            [2, 2, 2, 1, 1, 1, 1, 0, 1],
            [2, 2, 2, 2, 1, 1, 2, 1, 0]
        ]
        self.dicti = {} 
        self.keypad_to_dict()


    def keypad_to_dict(self):
        for i in range(len(self.keypad)):
            for j in range(len(self.keypad)):
                self.dicti[self.keypad[i]+'->'+self.keypad[j]] = self.distances[i][j]


    def solve(self):
        r = 0
        for i in range(len(self.sec_code)-1):
            r += self.dicti[self.sec_code[i]+'->'+self.sec_code[i+1]]

        return r


S = Solution()
print(S.solve())

